addEventHandler("onClientResourceStart", resourceRoot,	function ()		local vehicles = getElementsByType("vehicle")				setElementData(localPlayer, "parkedVehicle", false)		for i, vehicle in pairs(vehicles) do			if getElementData(vehicle, "owner") == getPlayerName(localPlayer) then				setElementData(localPlayer, "parkedVehicle", vehicle)				--outputChatBox("You own a vehicle.", 255,128,0)			end		end		--outputChatBox("#vehicles: "..#vehicles, 255,128,0)	end)function dudeWheresMyCar(playerSource, commandName)	local vehicle = getElementData(localPlayer, "parkedVehicle")		if vehicle then		local x,y,z = getElementPosition(vehicle)		outputChatBox(getZoneName(x,y,z,true)..", "..getZoneName(x,y,z,false), 0,255,0)--		outputChatBox("x: ".. math.modf(x).." y: ".. math.modf(y).." z: ".. math.modf(z), 0,255,0)	else		outputChatBox("You do not own a vehicle.", 255,0,0)	endendaddCommandHandler("dudeWheresMyCar", dudeWheresMyCar, false)function closeVehicleDoors()	for door = 0,5 do		local doorState = getVehicleDoorState(source, door)		if doorState == 1 then			setTimer(setVehicleDoorState, 50, 1, source, door, 0)		elseif doorState == 3 then			setTimer(setVehicleDoorState, 50, 1, source, door, 2)		end		setVehicleDoorOpenRatio(source, door, 0)	endendaddEventHandler("onClientVehicleEnter", getRootElement(), closeVehicleDoors)addEventHandler("onClientVehicleExit", getRootElement(), closeVehicleDoors)local vehicleBlips = {}local blipsOn = falsefunction vBlips(playerSource, commandName)	if not blipsOn then		for i, vehicle in pairs(getElementsByType("vehicle")) do			table.insert(vehicleBlips, createBlipAttachedTo(vehicle, 55, 1, 255,0,0,255, 0, 255))		end		blipsOn = true	else		for i, blip in pairs(vehicleBlips) do			destroyElement(blip)		end		vehicleBlips = {}		blipsOn = false	endendaddCommandHandler("vBlips", vBlips, false)